const helpers = require('../src');

function autoResolver() {
  return '/'
}

test('helpers are defined', () => {
  ['Link', 'Date'].forEach((helper) => {
    expect(helpers[helper]).not.toBe(undefined)
  })
})

test('all link types are supported', () => {
  const documentLinks = require('./__mocks__/documentLinks')
  const urls = documentLinks.map((link) => helpers.Link.url(link, autoResolver))
  expect(urls).toEqual(new Array(documentLinks.length).fill(autoResolver()))
})

